main_html_app = """
<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Formulaire d'envoi de fichier</title>
    <link href="./prism.css" rel="stylesheet" />
    <style>
        body, html {
            height: 100%;
            margin: 0;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .container {
            display: flex;
            flex-direction: column; /* Les éléments sont disposés de haut en bas */
            align-items: center; /* Centrage horizontal des éléments */
        }
        form {
            width: 300px; /* Largeur du formulaire */
            padding: 20px; /* Marge intérieure du formulaire */
            border: 1px solid #ccc; /* Bordure du formulaire */
            border-radius: 5px; /* Coins arrondis */
            margin-bottom: 20px; /* Marge entre les formulaires */
        }
        label, input[type="file"], input[type="submit"] {
            width: 100%; /* Largeur des éléments */
            box-sizing: border-box; /* Inclure la bordure et le padding dans la largeur */
            margin-bottom: 10px; /* Marge entre les éléments */
        }
        input[type="submit"] {
            background-color: #4CAF50; /* Couleur de fond du bouton */
            color: white; /* Couleur du texte du bouton */
            padding: 10px 20px; /* Padding du bouton */
            border: none; /* Suppression de la bordure */
            border-radius: 3px; /* Coins arrondis */
            cursor: pointer; /* Curseur pointer au survol */
        }
    </style>
</head>
<body>

<div class="container">
    <h2>Formulaire d'envoi de fichier</h2>

    <form action="/upload" method="post" enctype="multipart/form-data">
        <label for="fichier">Sélectionner un fichier :</label><br>
        <input type="file" id="fichier" name="fichier"><br>
        <label for="fichier">Choisisser un fichier à éditer</label><br>
        <input type="text" id="path" name="path"><br><br>
        <input type="submit" value="Envoyer">
    </form>

    <form action="/neuron" method="get">
        <input type="submit" value="CODE NEURONE">
    </form>

    <form action="/uart" method="get">
        <input type="submit" value="CODE UART">
    </form>
    
</div>

<script src="./prism.js"></script>

</body>
</html>
"""